import React, { useState, useEffect } from 'react';
import { ArticleCard } from './ArticleCard';
import { getAllArticles, deleteArticle } from '../Modules/ArticleManager'
import { useNavigate } from 'react-router-dom';

export const ArticleList = () => {
    const [articles, setArticles] = useState([]);
  
    const getArticles = () => {
      return getAllArticles().then(articlesFromAPI => {
        setArticles(articlesFromAPI)
      });
    };

    const handleDeleteArticle = id => {
      deleteArticle(id)
      .then(() => getAllArticles().then(setArticles));
  };
  
     useEffect(() => {
       getArticles();
     }, []);

    const navigate = useNavigate();
  
    return (
      <>
        <section className="section-content">
          <button type="button" className="btn" onClick={() => {navigate("/articles/create")}}>Post Article</button>
        </section>
        <div className="container-cards">
          {articles.map(article => <ArticleCard key={article.id} article={article} handleDeleteArticle={handleDeleteArticle} />)}
        </div>
      </>
    );
  };
