export const PostList = () => {
    const [posts, setPost] = useState([]);
  
    const getPosts = () => {
      return getAllPosts().then(postsFromAPI => {
        setPosts(postsFromAPI)
      });
    };

    const handleDeletePost = id => {
      deletePost(id)
      .then(() => getAllPosts().then(setPosts));
